using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEditor;
using System;
using System.IO;

namespace Looxid.Link
{
    [CustomEditor(typeof(LooxidLinkManager))]
    public class LooxidLinkDataReceiverEditor : Editor
    {
        private Texture2D masktexture;
        private Texture2D senserontexture;
        private Texture2D senserofftexture;

        private string mask64 = "";
        private string sensoron64 = "";
        private string sensoroff64 = "";

        private Rect AF3Rect;
        private Rect AF4Rect;
        private Rect Fp1Rect;
        private Rect Fp2Rect;
        private Rect AF7Rect;
        private Rect AF8Rect;

        public override bool RequiresConstantRepaint()
        {
            return true;
        }

        public override void OnInspectorGUI()
        {
            base.OnInspectorGUI();

            LooxidLinkManager manager = (LooxidLinkManager)target;

            GUIStyle backgroundStyle = new GUIStyle("WhiteBackground");
            backgroundStyle.stretchWidth = true;
            backgroundStyle.fixedHeight = 196;

            EditorGUILayout.Space();

            EditorGUILayout.BeginVertical();
            EditorGUILayout.BeginHorizontal();
            EditorGUILayout.LabelField("Looxid Core");
            if (manager.isLinkCoreConnected)
            {
                EditorGUILayout.Toggle(true);
                EditorGUILayout.LabelField(" Connected");
            }
            else
            {
                EditorGUILayout.Toggle(false);
                EditorGUILayout.LabelField(" Disconnected");
            }
            EditorGUILayout.EndHorizontal();
            EditorGUILayout.BeginHorizontal();
            EditorGUILayout.LabelField("Looxid Hub");
            if (manager.isLinkHubConnected)
            {
                EditorGUILayout.Toggle(true);
                EditorGUILayout.LabelField(" Connected");
            }
            else
            {
                EditorGUILayout.Toggle(false);
                EditorGUILayout.LabelField(" Disconnected");
            }
            EditorGUILayout.EndHorizontal();
            EditorGUILayout.EndVertical();
            EditorGUILayout.Space();

            Rect linerect = EditorGUILayout.GetControlRect(false, 1);
            linerect.height = 1;
            EditorGUI.DrawRect(linerect, new Color(1.0f, 1.0f, 1.0f, 0.2f));
            EditorGUILayout.Space();

            if (masktexture == null)
            {
                byte[] decodedBytes = Convert.FromBase64String(mask64);
                masktexture = new Texture2D(256, 256, TextureFormat.DXT1, false);
                masktexture.LoadImage(decodedBytes);
                masktexture.Apply();
            }
            if (senserontexture == null)
            {
                byte[] decodedBytes = Convert.FromBase64String(sensoron64);
                senserontexture = new Texture2D(16, 16, TextureFormat.RGB24, false);
                senserontexture.LoadImage(decodedBytes);
                senserontexture.Apply();
            }
            if (senserofftexture == null)
            {
                byte[] decodedBytes = Convert.FromBase64String(sensoroff64);
                senserofftexture = new Texture2D(16, 16, TextureFormat.RGB24, false);
                senserofftexture.LoadImage(decodedBytes);
                senserofftexture.Apply();
            }

            Rect rect = EditorGUILayout.GetControlRect(false, 1);

            Color oldColor = GUI.backgroundColor;
            GUI.backgroundColor = new Color(0.17255f, 0.17647f, 0.17255f, 1.0f);
            GUILayout.Button("", backgroundStyle);
            GUI.backgroundColor = oldColor;

            float rect_x = rect.x + (rect.width / 2 - 98);
            float rect_y = rect.y + rect.height + 4;
            GUI.DrawTexture(new Rect(rect_x, rect_y, 196, 196), masktexture);

            oldColor = GUI.backgroundColor;
            GUI.contentColor = new Color(1.0f, 1.0f, 1.0f, 1.0f);
            EditorGUI.LabelField(new Rect(rect_x - 26, rect_y + 20, 60, 16), "AF3");
            EditorGUI.LabelField(new Rect(rect_x - 26, rect_y + 61, 60, 16), "AF7");
            EditorGUI.LabelField(new Rect(rect_x - 26, rect_y + 79, 60, 16), "Fp1");
            EditorGUI.LabelField(new Rect(rect_x + 197, rect_y + 20, 60, 16), "AF4");
            EditorGUI.LabelField(new Rect(rect_x + 197, rect_y + 61, 60, 16), "AF8");
            EditorGUI.LabelField(new Rect(rect_x + 197, rect_y + 79, 60, 16), "Fp2");
            GUI.contentColor = oldColor;

            AF3Rect = new Rect(rect_x + 66, rect_y + 38, 12, 12);
            AF4Rect = new Rect(rect_x + 119, rect_y + 38, 12, 12);
            Fp1Rect = new Rect(rect_x + 76, rect_y + 68, 12, 12);
            Fp2Rect = new Rect(rect_x + 108, rect_y + 68, 12, 12);
            AF7Rect = new Rect(rect_x + 46, rect_y + 63, 12, 12);
            AF8Rect = new Rect(rect_x + 138, rect_y + 63, 12, 12);

            if (manager.isLinkCoreConnected && manager.isLinkHubConnected && manager.sensorStatusData != null)
            {
                EditorGUI.DrawPreviewTexture(AF3Rect, manager.sensorStatusData.IsSensorOn(EEGSensorID.AF3) ? senserontexture : senserofftexture);

                EditorGUI.DrawPreviewTexture(AF7Rect, manager.sensorStatusData.IsSensorOn(EEGSensorID.AF7) ? senserontexture : senserofftexture);

                EditorGUI.DrawPreviewTexture(Fp1Rect, manager.sensorStatusData.IsSensorOn(EEGSensorID.Fp1) ? senserontexture : senserofftexture);

                EditorGUI.DrawPreviewTexture(AF4Rect, manager.sensorStatusData.IsSensorOn(EEGSensorID.AF4) ? senserontexture : senserofftexture);

                EditorGUI.DrawPreviewTexture(AF8Rect, manager.sensorStatusData.IsSensorOn(EEGSensorID.AF8) ? senserontexture : senserofftexture);

                EditorGUI.DrawPreviewTexture(Fp2Rect, manager.sensorStatusData.IsSensorOn(EEGSensorID.Fp2) ? senserontexture : senserofftexture);
            }
            else
            {
                EditorGUI.DrawPreviewTexture(AF3Rect, senserofftexture);
                EditorGUI.DrawPreviewTexture(AF4Rect, senserofftexture);
                EditorGUI.DrawPreviewTexture(Fp1Rect, senserofftexture);
                EditorGUI.DrawPreviewTexture(Fp2Rect, senserofftexture);
                EditorGUI.DrawPreviewTexture(AF7Rect, senserofftexture);
                EditorGUI.DrawPreviewTexture(AF8Rect, senserofftexture);
            }

            EditorGUILayout.Space();
        }
    }
}